<style>
    :root {
        --txt-clr: #ffffff; 
        --bg-clr: #727176;
        --s-radius: 10px;
        --shadow: #323232;
        --popOfColour: #ff69b4; 
    }

    @font-face {
        font-family: "Noto sans";
        src: url('/MyAssets/NotoSans-VariableFont_wdth,wght.ttf');
    }

     main {        
        font-family: "Noto sans";
        color: var(--txt-clr);
        display: flex;
        justify-content: center;
    }

    h1 {
        letter-spacing: .2rem;
    }

    #card {
        background: var(--bg-clr);
        height: 15em;
        width: 21em;
        opacity: 0.6;
        padding: 1em;
        border-radius: var(--s-radius);
        box-shadow: var(--shadow) 7px 9px 6px ;
        display: grid;
    }

    svg {
        fill: var(--txt-clr);
    }

    svg:hover {
        fill: var(--popOfColour);
        filter: drop-shadow(7px 9px 6px var(--popOfColour));
    }

    @media (min-width: 700px) {

        main {
        min-height: 100dvh;
        display: flex;
        justify-content: center;
        align-items: center;
        }

    #card {
        width: 25em;
        height: 15em;
        display: grid;  grid-template-columns: 3fr 0fr 1fr; 
        grid-template-rows: 1fr 2fr 0.5fr; 
        box-shadow: var(--shadow) 7px 9px 6px ;
        position: relative;
    }

    h1 {
        grid-row: 1 / 2;
        grid-column: 1 / 2;
        margin: 0;
    }

    p {
        min-width: 15em;
        grid-row: 2 / 3;
        grid-column: 1 / 2;
    }

    #links a:nth-of-type(1){
        position: absolute;
        bottom: 0em;
        right: 6em;
    }

    #links a:nth-of-type(3){
        position: absolute;
        bottom: 0em;
        right: 0em;
    }

    #links a:nth-of-type(2){
        position: absolute;
        bottom: 0em;
        right: 3em;
    }

    #links a:nth-of-type(4){
        position: absolute;
        bottom: 3em;
        right: 0em;
    }

    #links a:nth-of-type(5){
        position: absolute;
        bottom: 6em;
        right: 0em;
    }
    
    .star {
        position: relative;
        width: 50px;
        height: 50px;
        background-color: var(--txt-clr);
        margin: 20px;
        clip-path: polygon(46% 50%, 50% 14%, 56% 49%, 88% 54%, 57% 60%, 50% 100%, 45% 61%, 9% 52%);
            animation: starAnimation 4s infinite ease-in-out;
        box-shadow: 0 0 10px var(--popOfColour);
    }

    .star:nth-last-of-type(1){
        position: absolute;
        top: 2em;
        right: 6em;
    }

    .star:nth-last-of-type(2){
        position: absolute;
        top: 0em;
        right: 0em;
    }

    .star:nth-last-of-type(3){
        position: absolute;
        top: 6em;
        right: 3em;
    }

        @keyframes starAnimation {
        0%, 100% { opacity: 0; }
        50% { opacity: 0.8; }
    }
}
   
</style>

